mapper.select:
SELECT p1.id,p1.title,p1.keyword,p1.stage,p1.spj,p1.time_limit,p1.memory_limit,p1.difficulty,p1.accepted, p1.solved,p1.submit,p1.submit_user, p2.name AS "catelog_name",p3.nickname AS "author" FROM problem p1 LEFT JOIN catelog p2 ON p1.catelog = p2.id LEFT JOIN user p3 ON p1.author_id = p3.id WHERE p1.contest_id = -1
and p1.stage = #{stage}
and (p1.id LIKE #{search} or p1.title LIKE #{search} or p1.keyword LIKE #{search})
ORDER BY p1.id ${direction}

