mapper.insert:
insert into article (id, title, user_id, 
  nickname, keyword, catelog_id, 
  totop, isshow, createdate, 
  modifydate, summary, content
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{userId,jdbcType=INTEGER}, 
  #{nickname,jdbcType=VARCHAR}, #{keyword,jdbcType=VARCHAR}, #{catelogId,jdbcType=INTEGER}, 
  #{totop,jdbcType=TINYINT}, #{isshow,jdbcType=TINYINT}, #{createdate,jdbcType=TIMESTAMP}, 
  #{modifydate,jdbcType=TIMESTAMP}, #{summary,jdbcType=LONGVARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT 
FROM article ORDER by createdate DESC

  limit ${value}
